from Crypto.Util.number import *

FLAG = b'FLAG{Random flag}'
g = getRandomNBitInteger(512)
print('g =', g)

while True:
    pr = getRandomNBitInteger(512)
    p = 2 * g * pr + 1
    if isPrime(p):
        break

while True:
    qr = getRandomNBitInteger(512)
    q = 2 * g * qr + 1
    if isPrime(q):
        break

n = p * q 
e = 0x10001
print('n =', n)
print('c =', pow(bytes_to_long(FLAG), e, n))
print(pr, qr)

